// src/app.jsx
import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/login";
import Register from "./pages/register";
import Dashboard from "./pages/dashboard";
import Profile from "./pages/profile";
import { verifyToken, logout } from "./api";

export default function App() {
  const [checking, setChecking] = useState(true);
  const [authed, setAuthed] = useState(false);

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const ok = await verifyToken();
        if (mounted) setAuthed(ok);
      } catch (e) {
        if (mounted) setAuthed(false);
      } finally {
        if (mounted) setChecking(false);
      }
    })();
    return () => (mounted = false);
  }, []);

  if (checking) {
    return <div style={{padding:20}}>Loading...</div>;
  }

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={authed ? <Navigate to="/dashboard" /> : <Navigate to="/login" />} />
        <Route path="/login" element={<Login onAuth={() => setAuthed(true)} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/dashboard" element={authed ? <Dashboard /> : <Navigate to="/login" />} />
        <Route path="/profile" element={authed ? <Profile /> : <Navigate to="/login" />} />
        <Route path="*" element={<Navigate to={authed ? "/dashboard" : "/login"} />} />
      </Routes>
      {/* optional global logout button */}
      {authed && (
        <div style={{ position: "fixed", right: 10, bottom: 10 }}>
          <button onClick={() => { logout(); }}>Logout</button>
        </div>
      )}
    </BrowserRouter>
  );
}
