// src/Dashboard.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const Dashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem("access_token"); // Assuming JWT stored here
        const response = await axios.get("http://localhost:8000/jobs", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        // Ensure jobs is an array
        const data = response.data;
        setJobs(Array.isArray(data) ? data : data.jobs || []);
      } catch (err) {
        console.error(err);
        setError("Failed to load jobs. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchJobs();
  }, []);

  if (loading) {
    return <div className="text-center mt-10">Loading jobs...</div>;
  }

  if (error) {
    return <div className="text-center mt-10 text-red-600">{error}</div>;
  }

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">Job Dashboard</h1>

      {jobs.length === 0 ? (
        <p>No jobs available.</p>
      ) : (
        <table className="min-w-full bg-white border border-gray-300">
          <thead className="bg-gray-100">
            <tr>
              <th className="py-2 px-4 border-b">Job Title</th>
              <th className="py-2 px-4 border-b">Company</th>
              <th className="py-2 px-4 border-b">Location</th>
              <th className="py-2 px-4 border-b">Apply Link</th>
              <th className="py-2 px-4 border-b">Posted At</th>
            </tr>
          </thead>
          <tbody>
            {(Array.isArray(jobs) ? jobs : []).map((job, index) => (
              <tr key={index} className="hover:bg-gray-50">
                <td className="py-2 px-4 border-b">{job.title}</td>
                <td className="py-2 px-4 border-b">{job.company}</td>
                <td className="py-2 px-4 border-b">{job.location}</td>
                <td className="py-2 px-4 border-b">
                  <a
                    href={job.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-500 underline"
                  >
                    Apply
                  </a>
                </td>
                <td className="py-2 px-4 border-b">{job.posted_at}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Dashboard;
