import React, {useEffect, useState} from "react";
import { getProfile, saveProfile } from "../api";
import { useNavigate, Link } from "react-router-dom";

const DOMAINS = ["Data Science","Software Engineering","DevOps","Security","Product","Analytics"];
const ROLES = ["Data Scientist","ML Engineer","Data Engineer","Backend Engineer","Frontend Engineer","DevOps"];
const WORK_MODES = ["Remote","Hybrid","Onsite"];
const US_STATES = ["CA","TX","NY","WA","FL","IL","GA","PA","OH","NC","NJ","VA","AZ","MA"]; // sample list; expand as needed
const VISA = ["","Yes","No","Yes in future"];

export default function Profile(){
  const [profile,setProfile] = useState({ domains:[], roles:[], experience:"", work_modes:[], locations:[], visa_sponsorship:"", email_opt_in:true });
  const [loading,setLoading] = useState(true);
  const nav = useNavigate();

  useEffect(()=>{
    getProfile().then(data=>{
      if(!data || !data.id){ nav("/login"); return; }
      setProfile({
        domains: data.domains||[],
        roles: data.roles||[],
        experience: data.experience||"",
        work_modes: data.work_modes||[],
        locations: data.locations||[],
        visa_sponsorship: data.visa_sponsorship||"",
        email_opt_in: data.email_opt_in===undefined ? true : !!data.email_opt_in
      });
    }).finally(()=>setLoading(false));
  },[nav]);

  function toggle(arrName, val){
    setProfile(prev=>{
      const s = new Set(prev[arrName]||[]);
      if(s.has(val)) s.delete(val); else s.add(val);
      return {...prev, [arrName]: Array.from(s)};
    });
  }

  async function submit(e){
    e.preventDefault();
    const payload = {
      domains: profile.domains,
      roles: profile.roles,
      experience: profile.experience ? Number(profile.experience) : null,
      work_modes: profile.work_modes,
      locations: profile.locations,
      visa_sponsorship: profile.visa_sponsorship,
      email_opt_in: profile.email_opt_in
    };
    const res = await saveProfile(payload);
    if(res && res.success) {
      alert("Saved. Redirecting to dashboard.");
      nav("/dashboard");
    } else {
      alert("Save failed");
    }
  }

  if(loading) return <div className="container">Loading...</div>;

  return (
    <div className="container">
      <nav><Link to="/dashboard">Dashboard</Link> | <Link to="/profile">Profile</Link></nav>
      <h2>Profile Preferences</h2>
      <form onSubmit={submit}>
        <div className="form-row"><label>Domains (multi)</label>
          <div>{DOMAINS.map(d => (
            <label key={d} style={{marginRight:12}}>
              <input type="checkbox" checked={profile.domains.includes(d)} onChange={()=>toggle("domains",d)} /> {d}
            </label>
          ))}</div>
        </div>

        <div className="form-row"><label>Roles (multi)</label>
          <div>{ROLES.map(r => (
            <label key={r} style={{marginRight:12}}>
              <input type="checkbox" checked={profile.roles.includes(r)} onChange={()=>toggle("roles",r)} /> {r}
            </label>
          ))}</div>
        </div>

        <div className="form-row"><label>Experience (years)</label>
          <input type="number" value={profile.experience} onChange={e=>setProfile({...profile, experience:e.target.value})} />
        </div>

        <div className="form-row"><label>Work modes (multi)</label>
          <div>{WORK_MODES.map(m=>(
            <label key={m} style={{marginRight:12}}>
              <input type="checkbox" checked={profile.work_modes.includes(m)} onChange={()=>toggle("work_modes",m)} /> {m}
            </label>
          ))}</div>
        </div>

        <div className="form-row"><label>Locations (US states multi)</label>
          <div>{US_STATES.map(s=>(
            <label key={s} style={{marginRight:12}}>
              <input type="checkbox" checked={profile.locations.includes(s)} onChange={()=>toggle("locations",s)} /> {s}
            </label>
          ))}</div>
        </div>

        <div className="form-row">
          <label>Visa sponsorship</label>
          <select value={profile.visa_sponsorship} onChange={e=>setProfile({...profile, visa_sponsorship:e.target.value})}>
            {VISA.map(v=> <option key={v} value={v}>{v||"— select —"}</option>)}
          </select>
        </div>

        <div className="form-row">
          <label><input type="checkbox" checked={profile.email_opt_in} onChange={e=>setProfile({...profile, email_opt_in:e.target.checked})} /> Receive email alerts</label>
        </div>

        <div style={{display:"flex",gap:8}}>
          <button type="submit">Save & Go to Dashboard</button>
        </div>
      </form>
    </div>
  );
}
