// src/pages/dashboard.jsx
import React, { useEffect, useState } from "react";
import { fetchJobs, getProfile } from "../api";
import { Link, useNavigate } from "react-router-dom";

export default function Dashboard() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [prefMissing, setPrefMissing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const profile = await getProfile();
        if (!profile) {
          // Not logged in, api will redirect; extra safety:
          navigate("/login");
          return;
        }
        // if profile seems empty, prompt user to fill it
        const hasPrefs = profile.domain || profile.role || profile.location || profile.work_mode || profile.experience;
        if (!hasPrefs) {
          setPrefMissing(true);
        } else {
          setPrefMissing(false);
        }

        const data = await fetchJobs();
        if (!mounted) return;
        setJobs(Array.isArray(data) ? data : []);
      } catch (e) {
        console.error("dashboard load error", e);
        setJobs([]);
      } finally {
        if (mounted) setLoading(false);
      }
    })();
    return () => (mounted = false);
  }, [navigate]);

  if (loading) return <div className="container">Loading jobs...</div>;

  return (
    <div className="container">
      <nav>
        <Link to="/dashboard">Dashboard</Link> | <Link to="/profile">Profile</Link>
      </nav>

      <h2>Recommended Jobs</h2>

      {prefMissing && (
        <div style={{ background: "#fffbdd", padding: 10, marginBottom: 12 }}>
          Your profile preferences are empty. <Link to="/profile">Set your profile</Link> to get better matches.
        </div>
      )}

      {jobs.length === 0 ? (
        <p>No jobs found. Try updating your profile or check backend connectivity.</p>
      ) : (
        <ul>
          {jobs.map((job, idx) => (
            <li key={job.id || job.url || idx} style={{marginBottom:12}}>
              <div style={{fontWeight:700}}>{job.title || job.role || "Untitled"}</div>
              <div>{job.company || job.company_name || ""} — {job.location || ""}</div>
              <div>Mode: {job.work_mode || job.workplace_type || "—"}</div>
              <div style={{marginTop:6}}>
                <a href={job.url || job.job_url || job.link || "#"} target="_blank" rel="noreferrer">Apply</a>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
