// frontend/src/pages/profile.jsx
import React, { useEffect, useState } from "react";
import { getProfile, saveProfile } from "../api";
import { useNavigate } from "react-router-dom";

const ALL_DOMAINS = ["Data Science", "Software Engineering", "DevOps", "Product", "Analytics"];
const ALL_ROLES = ["Data Scientist", "ML Engineer", "Data Engineer", "Backend Engineer", "Frontend Engineer", "DevOps Engineer"];
const US_STATES = ["CA","TX","NY","WA","FL","IL","GA","PA","OH","MI"]; // sample, expand as needed
const WORK_MODES = ["Remote","Hybrid","Onsite"];
const VISA = ["Yes","No","Yes in future"];

export default function Profile() {
  const nav = useNavigate();
  const [pref, setPref] = useState({
    domains: [], roles: [], locations: [], experience: "", work_mode: "", visa_sponsorship: "", email_opt_in: true
  });
  const [loading, setLoading] = useState(true);
  const [msg, setMsg] = useState("");

  useEffect(() => {
    getProfile().then(d => {
      if (!d) { nav("/login"); return; }
      // d may be PreferenceOut-like or empty container
      setPref({
        domains: d.domains || [],
        roles: d.roles || [],
        locations: d.locations || [],
        experience: d.experience || "",
        work_mode: d.work_mode || "",
        visa_sponsorship: d.visa_sponsorship || "",
        email_opt_in: d.email_opt_in !== undefined ? d.email_opt_in : true
      });
      setLoading(false);
    }).catch(()=> nav("/login"));
  }, [nav]);

  function toggle(field, value) {
    setPref(prev => {
      const s = new Set(prev[field] || []);
      if (s.has(value)) s.delete(value); else s.add(value);
      return { ...prev, [field]: Array.from(s) };
    });
  }

  async function submit(e) {
    e.preventDefault();
    setMsg("");
    try {
      const payload = { ...pref };
      const res = await saveProfile(payload);
      if (res && res.success) {
        setMsg("Saved");
        nav("/dashboard");
      } else {
        setMsg("Save failed");
      }
    } catch (err) {
      setMsg("Save failed");
      console.error(err);
    }
  }

  if (loading) return <div className="container">Loading...</div>;

  return (
    <div className="container">
      <nav><button onClick={()=>nav("/dashboard")}>Dashboard</button></nav>
      <h2>Profile Preferences</h2>
      <form onSubmit={submit}>
        <div><label>Domains</label><div className="multi">
          {ALL_DOMAINS.map(d => <label key={d} className="tag"><input type="checkbox" checked={pref.domains.includes(d)} onChange={() => toggle("domains", d)}/> {d}</label>)}
        </div></div>

        <div><label>Roles</label><div className="multi">
          {ALL_ROLES.map(r => <label key={r} className="tag"><input type="checkbox" checked={pref.roles.includes(r)} onChange={() => toggle("roles", r)}/> {r}</label>)}
        </div></div>

        <div><label>Locations (US states)</label><div className="multi">
          {US_STATES.map(s => <label key={s} className="tag"><input type="checkbox" checked={pref.locations.includes(s)} onChange={() => toggle("locations", s)}/> {s}</label>)}
        </div></div>

        <div className="form-row"><input placeholder="Experience (years)" value={pref.experience} onChange={e=>setPref({...pref, experience: Number(e.target.value)})} type="number" /></div>

        <div className="form-row">
          <label>Work mode</label>
          <select value={pref.work_mode} onChange={e=>setPref({...pref, work_mode: e.target.value})}>
            <option value="">Any</option>
            {WORK_MODES.map(w => <option key={w} value={w}>{w}</option>)}
          </select>
        </div>

        <div className="form-row">
          <label>Visa sponsorship</label>
          <select value={pref.visa_sponsorship} onChange={e=>setPref({...pref, visa_sponsorship: e.target.value})}>
            <option value="">Any</option>
            {VISA.map(v => <option key={v} value={v}>{v}</option>)}
          </select>
        </div>

        <div className="form-row">
          <label><input type="checkbox" checked={pref.email_opt_in} onChange={e=>setPref({...pref, email_opt_in: e.target.checked})} /> Email notifications for new matches</label>
        </div>

        <div className="form-row"><button className="primary" type="submit">Save preferences</button></div>
      </form>
      {msg && <p>{msg}</p>}
    </div>
  );
}
