import React, { useEffect, useState } from "react";

export default function Dashboard() {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/api/jobs/jobright")
      .then(res => res.json())
      .then(data => {
        console.log("Jobs:", data.jobs);
        setJobs(data.jobs || []);
      })
      .catch(err => console.error("Error fetching jobs:", err));
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Latest Jobs</h2>

      {jobs.length === 0 ? (
        <p>No jobs found.</p>
      ) : (
        <table
          style={{
            width: "100%",
            borderCollapse: "collapse",
            marginTop: "20px",
          }}
        >
          <thead>
            <tr style={{ background: "#f0f0f0" }}>
              <th style={thStyle}>Role</th>
              <th style={thStyle}>Company</th>
              <th style={thStyle}>Location</th>
              <th style={thStyle}>Details</th>
              <th style={thStyle}>Apply</th>
            </tr>
          </thead>

          <tbody>
            {jobs.map((job, index) => (
              <tr key={index} style={{ borderBottom: "1px solid #ddd" }}>
                <td style={tdStyle}>{job.jobTitle}</td>
                <td style={tdStyle}>{job.companyName}</td>
                <td style={tdStyle}>{job.location}</td>
                <td style={tdStyle}>
                  <button
                    onClick={() => alert(job.jobSummary || "No details")}
                    style={btnStyle}
                  >
                    View
                  </button>
                </td>
                <td style={tdStyle}>
                  <a
                    href={job.applyLink}
                    target="_blank"
                    rel="noreferrer"
                    style={{ ...btnStyle, background: "#4CAF50" }}
                  >
                    Apply
                  </a>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

const thStyle = {
  textAlign: "left",
  padding: "10px",
  borderBottom: "2px solid #ccc",
  fontWeight: "bold",
};

const tdStyle = {
  padding: "10px",
};

const btnStyle = {
  padding: "6px 12px",
  background: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
  textDecoration: "none",
};
