import React, { useEffect, useState } from "react";

export default function ProfileForm() {
  const [workModes, setWorkModes] = useState([]);
  const [yoe, setYoe] = useState(0);
  const [domains, setDomains] = useState("");
  const token = localStorage.getItem("access_token");

  useEffect(() => {
    // fetch existing profile
    if (!token) return;
    fetch("http://localhost:8000/api/profile/me", {
      headers: {"Authorization": `Bearer ${token}`}
    })
      .then(res => {
        if (!res.ok) return null;
        return res.json();
      })
      .then(data => {
        if (!data) return;
        setWorkModes(data.work_modes || []);
        setYoe(data.yoe || 0);
        setDomains((data.domains || []).join(", "));
      })
      .catch(()=>{});
  }, [token]);

  const submit = async (e) => {
    e.preventDefault();
    const body = {
      work_modes: workModes,
      yoe: Number(yoe),
      domains: domains.split(",").map(s => s.trim()).filter(Boolean),
      location_pref: ""
    };
    const res = await fetch("http://localhost:8000/api/profile/me", {
      method: "POST",
      headers: {
        "Content-Type":"application/json",
        "Authorization": `Bearer ${token}`
      },
      body: JSON.stringify(body)
    });
    if (res.ok) alert("Saved");
    else alert("Save failed");
  };

  return (
    <form onSubmit={submit} style={{maxWidth:600}}>
      <h3>Profile</h3>
      <label>Work modes (comma separated, e.g. remote,hybrid,onsite)</label>
      <input value={workModes.join(",")} onChange={e => setWorkModes(e.target.value.split(",").map(s=>s.trim()))} />
      <label>Years of experience</label>
      <input type="number" value={yoe} onChange={e=>setYoe(e.target.value)} />
      <label>Domains (comma separated)</label>
      <input value={domains} onChange={e=>setDomains(e.target.value)} />
      <button type="submit">Save</button>
    </form>
  );
}
