import React, { useState, useEffect } from "react";
import Register from "./pages/Register";
import Login from "./pages/Login";
import ProfileForm from "./pages/ProfileForm";
import Dashboard from "./pages/Dashboard";

export default function App() {

  const [page, setPage] = useState("login");
  const [token, setToken] = useState(localStorage.getItem("access_token"));

  // ✅ Fetch jobs on load
  useEffect(() => {
    fetch("http://localhost:8000/api/jobs/jobright/")
      .then(res => res.json())
      .then(data => console.log("Jobs from backend:", data.jobs))
      .catch(err => console.error("Fetch error:", err));
  }, []);

  function handleLogin(tokenStr){
    localStorage.setItem("access_token", tokenStr);
    setToken(tokenStr);
    setPage("profile");
  }

  function handleLogout(){
    localStorage.removeItem("access_token");
    setToken(null);
    setPage("login");
  }

  if (!token) {
    if (page === "register") return <Register onSwitch={()=>setPage("login")} />;
    return <Login onLogin={handleLogin} onSwitch={()=>setPage("register")} />;
  }

  return (
    <div style={{padding:20}}>
      <button onClick={handleLogout}>Logout</button>
      <h1>Job Aggregator</h1>

      <nav>
        <button onClick={()=>setPage("profile")}>Profile</button>
        <button onClick={()=>setPage("dashboard")}>Dashboard</button>
      </nav>
      <hr/>

      {page === "profile" && <ProfileForm />}
      {page === "dashboard" && <Dashboard />}
    </div>
  );
}
