import React, {useState} from "react";
import { useNavigate } from "react-router-dom";
import { registerUser } from "../api";

export default function Register(){
  const [name,setName]=useState("");
  const [email,setEmail]=useState("");
  const [password,setPassword]=useState("");
  const [repass,setRepass]=useState("");
  const [err,setErr]=useState("");
  const nav = useNavigate();

  async function submit(e){
    e.preventDefault();
    setErr("");
    if(password !== repass){ setErr("Passwords do not match"); return; }
    const res = await registerUser({ name, email, password });
    if (res && res.access_token) {
      localStorage.setItem("token", res.access_token);
      nav("/profile");
    } else {
      setErr(res.detail || "Registration failed");
    }
  }

  return (
    <div className="container">
      <h2>Register</h2>
      <form onSubmit={submit}>
        <div className="form-row"><label>Name</label><input value={name} onChange={e=>setName(e.target.value)} required/></div>
        <div className="form-row"><label>Email</label><input value={email} onChange={e=>setEmail(e.target.value)} required/></div>
        <div className="form-row"><label>Password</label><input type="password" value={password} onChange={e=>setPassword(e.target.value)} required/></div>
        <div className="form-row"><label>Retype password</label><input type="password" value={repass} onChange={e=>setRepass(e.target.value)} required/></div>
        <div style={{display:"flex",gap:8}}><button type="submit">Register</button></div>
        {err && <p style={{color:"red"}}>{err}</p>}
      </form>
    </div>
  );
}
