// src/Dashboard.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Dashboard() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const token = localStorage.getItem("access_token");
        const res = await fetch("http://localhost:8000/api/jobs/jobright", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        if (data.status !== "success") {
          setError("Failed to load jobs. Please try again.");
        } else {
          setJobs(Array.isArray(data.jobs) ? data.jobs : []);
        }
      } catch (err) {
        setError("Failed to load jobs. Please try again.");
      } finally {
        setLoading(false);
      }
    };
    fetchJobs();
  }, []);

  if (loading) return <div>Loading jobs...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Dashboard</h2>
      <button onClick={() => navigate("/profile")}>Profile</button>
      <button onClick={() => { localStorage.removeItem("access_token"); navigate("/login"); }}>Logout</button>
      <div>
        {jobs.length === 0 && <p>No jobs available.</p>}
        {jobs.map((job, index) => (
          <div key={index} style={{ border: "1px solid #ccc", margin: "10px", padding: "10px" }}>
            <h3>{job.title}</h3>
            <p><strong>Company:</strong> {job.company}</p>
            <p><strong>Location:</strong> {job.location}</p>
            <p><strong>Summary:</strong> {job.summary}</p>
            <a href={job.url} target="_blank" rel="noopener noreferrer">Apply</a>
          </div>
        ))}
      </div>
    </div>
  );
}
