// src/pages/Profile.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

// Example options
const DOMAIN_OPTIONS = ["Tech", "Finance", "Marketing", "Healthcare"];
const ROLE_OPTIONS = ["Software Engineer", "Analyst", "Manager", "Intern"];
const WORK_MODE_OPTIONS = ["Remote", "Hybrid", "Onsite"];
const VISA_OPTIONS = ["Yes", "No", "Yes in future"];
const US_STATES = [
  "AL","AK","AZ","AR","CA","CO","CT","DE","FL","GA","HI","ID","IL","IN",
  "IA","KS","KY","LA","ME","MD","MA","MI","MN","MS","MO","MT","NE","NV",
  "NH","NJ","NM","NY","NC","ND","OH","OK","OR","PA","RI","SC","SD","TN",
  "TX","UT","VT","VA","WA","WV","WI","WY"
];

const Profile = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [prefs, setPrefs] = useState({
    domains: [],
    roles: [],
    locations: [],
    years_experience: 0,
    work_modes: [],
    visa_sponsorship: "",
    email_opt_in: true,
  });

  const token = localStorage.getItem("access_token");

  // Fetch user preferences
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get("http://localhost:8000/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const data = res.data;

        // transform stored comma-separated strings back to arrays
        setPrefs({
          domains: data.domains ? data.domains.split(",") : [],
          roles: data.roles ? data.roles.split(",") : [],
          locations: data.locations ? data.locations.split(",") : [],
          years_experience: data.experience || 0,
          work_modes: data.work_mode ? [data.work_mode] : [],
          visa_sponsorship: data.visa_sponsorship || "",
          email_opt_in: data.email_opt_in ?? true,
        });
        setLoading(false);
      } catch (err) {
        console.error(err);
        // If token invalid, clear and redirect to login
        localStorage.removeItem("access_token");
        navigate("/login");
      }
    };
    fetchProfile();
  }, [token, navigate]);

  const handleChange = (e) => {
    const { name, value, type, checked, options } = e.target;

    if (type === "select-multiple") {
      const values = Array.from(options).filter((o) => o.selected).map((o) => o.value);
      setPrefs((prev) => ({ ...prev, [name]: values }));
    } else if (type === "checkbox") {
      setPrefs((prev) => ({ ...prev, [name]: checked }));
    } else {
      setPrefs((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        "http://localhost:8000/preferences",
        {
          domains: prefs.domains,
          roles: prefs.roles,
          locations: prefs.locations,
          experience: prefs.years_experience,
          work_mode: prefs.work_modes[0] || "",
          visa_sponsorship: prefs.visa_sponsorship,
          email_opt_in: prefs.email_opt_in,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Preferences saved successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to save preferences. Try again.");
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="profile-container">
      <h2>User Preferences</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Domains (multi-select):</label>
          <select name="domains" multiple value={prefs.domains} onChange={handleChange}>
            {DOMAIN_OPTIONS.map((d) => (
              <option key={d} value={d}>{d}</option>
            ))}
          </select>
        </div>

        <div>
          <label>Roles (multi-select):</label>
          <select name="roles" multiple value={prefs.roles} onChange={handleChange}>
            {ROLE_OPTIONS.map((r) => (
              <option key={r} value={r}>{r}</option>
            ))}
          </select>
        </div>

        <div>
          <label>Locations (US States multi-select):</label>
          <select name="locations" multiple value={prefs.locations} onChange={handleChange}>
            {US_STATES.map((s) => (
              <option key={s} value={s}>{s}</option>
            ))}
          </select>
        </div>

        <div>
          <label>Experience (years):</label>
          <input
            type="number"
            name="years_experience"
            value={prefs.years_experience}
            onChange={handleChange}
            min={0}
          />
        </div>

        <div>
          <label>Work Mode:</label>
          <select name="work_modes" value={prefs.work_modes} onChange={handleChange}>
            {WORK_MODE_OPTIONS.map((w) => (
              <option key={w} value={w}>{w}</option>
            ))}
          </select>
        </div>

        <div>
          <label>Visa Sponsorship:</label>
          <select name="visa_sponsorship" value={prefs.visa_sponsorship} onChange={handleChange}>
            {VISA_OPTIONS.map((v) => (
              <option key={v} value={v}>{v}</option>
            ))}
          </select>
        </div>

        <div>
          <label>
            <input
              type="checkbox"
              name="email_opt_in"
              checked={prefs.email_opt_in}
              onChange={handleChange}
            />
            Email notifications
          </label>
        </div>

        <button type="submit">Save Preferences</button>
      </form>
    </div>
  );
};

export default Profile;
