import React, { useEffect, useState } from "react";

export default function Dashboard({ token }) {
  const [profile, setProfile] = useState(null);
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);

  // Load logged-in user's profile
  useEffect(() => {
    if (!token) return;

    fetch("http://localhost:8000/api/profile/me", {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        console.log("Profile loaded:", data);
        setProfile(data);
      })
      .catch(err => console.error("Profile fetch error:", err));
  }, [token]);

  // Load jobs AFTER profile is loaded
  useEffect(() => {
    if (!profile) return;

    fetch("http://localhost:8000/api/jobs/jobright", {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        console.log("Raw jobs:", data.jobs);
        setJobs(data.jobs || []);

        const skillWords = profile.skills.toLowerCase().split(/[ ,]+/);
        const roleWords = profile.role.toLowerCase().split(/[ ,]+/);

        const matches = (title) => {
          if (!title) return false;
          const t = title.toLowerCase();
          return (
            skillWords.some(w => t.includes(w)) ||
            roleWords.some(w => t.includes(w))
          );
        };

        const filtered = data.jobs.filter(job => matches(job.title));

        console.log("Filtered jobs:", filtered);
        setFilteredJobs(filtered.length ? filtered : data.jobs);
      })
      .catch(err => console.error("Job fetch error:", err));
  }, [profile]);

  return (
    <div style={{ padding: 20 }}>
      <h2>Matched Jobs</h2>

      {filteredJobs.length === 0 ? (
        <p>No jobs found.</p>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th>Role</th>
              <th>Company</th>
              <th>Location</th>
              <th>Published</th>
              <th>Apply</th>
            </tr>
          </thead>
          <tbody>
            {filteredJobs.map((j, i) => (
              <tr key={i}>
                <td>{j.title}</td>
                <td>{j.company || "—"}</td>
                <td>{j.location}</td>
                <td>{j.published}</td>
                <td>
                  <a href={j.url} target="_blank" rel="noreferrer">
                    Apply
                  </a>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
