useEffect(() => {
  fetch("http://localhost:8000/api/jobs/jobright/")
    .then(res => res.json())
    .then(data => {
      console.log("RAW DATA:", data);

      // If backend returns {jobs: [...]}
      if (Array.isArray(data.jobs)) {
        setJobs(data.jobs);
      }
      // If backend returns [...] directly
      else if (Array.isArray(data)) {
        setJobs(data);
      }
      // If backend error or wrong format
      else {
        setJobs([]);
      }
    })
    .catch(err => {
      console.error("Error fetching jobs:", err);
      setJobs([]);
    });
}, []);
