// src/api.js
import axios from "axios";

const API_URL = "http://localhost:8000"; // adjust backend URL

export async function getProfile() {
  try {
    const res = await axios.get(`${API_URL}/profile`, { withCredentials: true });
    return res.data;
  } catch (err) {
    console.error("Error fetching profile", err);
    return null;
  }
}

export async function saveProfile(profile) {
  try {
    const res = await axios.post(`${API_URL}/profile`, profile, { withCredentials: true });
    return res.data;
  } catch (err) {
    console.error("Error saving profile", err);
    return null;
  }
}

export async function fetchJobs() {
  try {
    const res = await axios.get(`${API_URL}/jobs`, { withCredentials: true });
    return res.data;
  } catch (err) {
    console.error("Error fetching jobs", err);
    return [];
  }
}

export async function register(user) {
  try {
    const res = await axios.post(`${API_URL}/register`, user);
    return res.data;
  } catch (err) {
    console.error("Error registering user", err);
    return null;
  }
}

export async function login(credentials) {
  try {
    const res = await axios.post(`${API_URL}/login`, credentials, { withCredentials: true });
    return res.data;
  } catch (err) {
    console.error("Error logging in", err);
    return null;
  }
}
