import React, { useEffect, useState } from "react";

export default function Dashboard({ token }) {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    if (!token) {
      console.log("No token found");
      return;
    }

    console.log("Using token:", token);

    fetch("http://localhost:8000/api/jobs/jobright/", {
      headers: { "Authorization": `Bearer ${token}` }
    })
      .then(async res => {
        console.log("JobRight status:", res.status);

        if (res.status === 401) {
          console.error("Unauthorized — token invalid?");
          return { jobs: [] };
        }

        const data = await res.json();
        console.log("Raw jobs:", data);
        return data;
      })
      .then(data => {
        if (data && data.jobs) {
          setJobs(data.jobs);
        } else {
          console.log("No jobs array found");
          setJobs([]);
        }
      })
      .catch(err => console.error("Error fetching jobs:", err));
  }, [token]);

  return (
    <div style={{ padding: 20 }}>
      <h2>Latest Jobs</h2>

      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr><th>Role</th><th>Company</th><th>Location</th><th>Published</th><th>Apply</th></tr>
        </thead>
        <tbody>
          {jobs.length === 0 ? (
            <tr><td colSpan={5}>No jobs found.</td></tr>
          ) : jobs.map((j,i) => (
            <tr key={i}>
              <td>{j.title}</td>
              <td>{j.company || "—"}</td>
              <td>{j.location}</td>
              <td>{j.published}</td>
              <td><a href={j.url} target="_blank" rel="noreferrer">Apply</a></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
