// frontend/src/pages/dashboard.jsx
import React, { useEffect, useState } from "react";
import { fetchJobs } from "../api";
import { useNavigate } from "react-router-dom";

export default function Dashboard() {
  const nav = useNavigate();
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchJobs().then(list => {
      setJobs(list || []);
    }).catch(err => {
      console.error(err);
    }).finally(()=>setLoading(false));
  }, []);

  if (loading) return <div className="container">Loading jobs...</div>;

  return (
    <div className="container">
      <nav>
        <button onClick={() => nav("/dashboard")}>Dashboard</button>
        <button onClick={() => nav("/profile")}>Profile</button>
      </nav>

      <h2>Recommended Jobs</h2>
      {jobs.length === 0 ? <p>No matching jobs found.</p> : (
        jobs.map((j, idx) => (
          <div className="job-card" key={idx}>
            <div className="job-left">
              <div style={{fontWeight:600}}>{j.title}</div>
              <div className="small">{j.company} — {j.location}</div>
              <div className="small">{j.source} • {j.posted || ""}</div>
            </div>
            <div>
              {j.url ? <a href={j.url} target="_blank" rel="noreferrer"><button>Apply</button></a> : <button disabled>Apply</button>}
            </div>
          </div>
        ))
      )}
    </div>
  );
}
