// frontend/src/api.js
const BASE = process.env.REACT_APP_API_URL || "http://localhost:8000";

async function request(path, opts = {}) {
  const token = localStorage.getItem("token");
  const headers = opts.headers || {};
  if (token) headers["Authorization"] = `Bearer ${token}`;
  headers["Content-Type"] = headers["Content-Type"] || "application/json";

  const res = await fetch(BASE + path, {
    ...opts,
    headers,
  });
  if (res.status === 401) {
    // remove token if invalid
    localStorage.removeItem("token");
  }
  const text = await res.text();
  try {
    return JSON.parse(text || "{}");
  } catch {
    return text;
  }
}

export async function registerUser(payload) {
  return request("/register", { method: "POST", body: JSON.stringify(payload) });
}
export async function loginUser(payload) {
  return request("/login", { method: "POST", body: JSON.stringify(payload) });
}
export async function getProfile() {
  return request("/profile", { method: "GET" });
}
export async function saveProfile(profile) {
  return request("/profile", { method: "POST", body: JSON.stringify(profile) });
}
export async function fetchJobs() {
  return request("/api/jobs", { method: "GET" });
}
