import React, { useEffect, useState } from "react";
import { authFetch, logout } from "../api";
import { Link, useNavigate } from "react-router-dom";

export default function Profile() {
  const [profile, setProfile] = useState({
    email: "",
    domains: [],
    roles: [],
    experience: 0,
    work_mode: [],
    locations: [],
    visa_sponsorship: "",
    email_opt_in: true,
  });

  const navigate = useNavigate();

  useEffect(() => {
    const load = async () => {
      try {
        const data = await authFetch("/api/profile");
        setProfile({
          email: data.email,
          domains: data.domains || [],
          roles: data.roles || [],
          experience: data.experience || 0,
          work_mode: data.work_mode ? [data.work_mode] : [],
          locations: data.locations || [],
          visa_sponsorship: data.visa_sponsorship || "",
          email_opt_in: data.email_opt_in ?? true,
        });
      } catch (err) {
        navigate("/login");
      }
    };

    load();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      setProfile((prev) => ({ ...prev, [name]: checked }));
    } else {
      setProfile((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSave = async () => {
    try {
      await authFetch("/api/profile/update", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(profile),
      });
      alert("Preferences saved successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to save preferences");
    }
  };

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <div style={{ padding: 20 }}>
      <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h2>Profile</h2>
        <nav>
          <Link to="/dashboard" style={{ marginRight: 12 }}>Dashboard</Link>
          <Link to="/profile" style={{ marginRight: 12 }}>Profile</Link>
          <button onClick={handleLogout}>Logout</button>
        </nav>
      </header>

      <section style={{ marginTop: 20 }}>
        <label>
          Email:
          <input type="email" name="email" value={profile.email} readOnly />
        </label>

        <label>
          Domains (comma-separated):
          <input
            type="text"
            name="domains"
            value={profile.domains.join(", ")}
            onChange={(e) => setProfile((prev) => ({ ...prev, domains: e.target.value.split(",").map(d => d.trim()) }))}
          />
        </label>

        <label>
          Roles (comma-separated):
          <input
            type="text"
            name="roles"
            value={profile.roles.join(", ")}
            onChange={(e) => setProfile((prev) => ({ ...prev, roles: e.target.value.split(",").map(r => r.trim()) }))}
          />
        </label>

        <label>
          Experience (years):
          <input type="number" name="experience" value={profile.experience} onChange={handleChange} />
        </label>

        <label>
          Visa Sponsorship:
          <select name="visa_sponsorship" value={profile.visa_sponsorship} onChange={handleChange}>
            <option value="">Select</option>
            <option value="H1B">H1B</option>
            <option value="L1">L1</option>
            <option value="None">None</option>
          </select>
        </label>

        <label>
          Locations (comma-separated):
          <input
            type="text"
            name="locations"
            value={profile.locations.join(", ")}
            onChange={(e) => setProfile((prev) => ({ ...prev, locations: e.target.value.split(",").map(l => l.trim()) }))}
          />
        </label>

        <label>
          Work Mode (comma-separated):
          <input
            type="text"
            name="work_mode"
            value={profile.work_mode.join(", ")}
            onChange={(e) => setProfile((prev) => ({ ...prev, work_mode: e.target.value.split(",").map(w => w.trim()) }))}
          />
        </label>

        <label>
          Email Opt-In:
          <input type="checkbox" name="email_opt_in" checked={profile.email_opt_in} onChange={handleChange} />
        </label>

        <button onClick={handleSave} style={{ marginTop: 20 }}>
          Save Preferences
        </button>
      </section>
    </div>
  );
}
