// frontend/src/pages/Dashboard.jsx
import React, { useEffect, useState } from "react";
import { getJobs } from "../api";

export default function Dashboard() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [msg, setMsg] = useState("");

  useEffect(() => {
    let mounted = true;
    async function load() {
      setLoading(true);
      try {
        const res = await getJobs();
        if (!mounted) return;
        if (res && res.jobs) {
          setJobs(res.jobs);
          setMsg("");
        } else if (res && res.message) {
          setJobs([]);
          setMsg(res.message);
        } else {
          setJobs([]);
          setMsg("Failed to load jobs");
        }
      } catch (err) {
        console.error(err);
        setJobs([]);
        setMsg(err.detail || "Failed to load jobs");
      } finally {
        if (mounted) setLoading(false);
      }
    }
    load();
    return () => (mounted = false);
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h2>Recommended Jobs for You</h2>
      {loading ? <div>Loading jobs...</div> : (
        <>
          {msg && <div style={{ marginBottom: 10 }}>{msg}</div>}
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th>Role</th>
                <th>Company</th>
                <th>Location</th>
                <th>Published</th>
                <th>Source</th>
                <th>Apply</th>
              </tr>
            </thead>
            <tbody>
              {jobs.length === 0 ? (
                <tr><td colSpan={6}>No jobs found.</td></tr>
              ) : jobs.map((j, i) => (
                <tr key={i}>
                  <td>{j.title}</td>
                  <td>{j.company || "—"}</td>
                  <td>{j.location}</td>
                  <td>{j.published}</td>
                  <td style={{ textTransform: "capitalize" }}>{j.source}</td>
                  <td><a href={j.url || "#"} target="_blank" rel="noreferrer">Apply</a></td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}
