import React, {useEffect, useState} from "react";
import { getMyProfile, saveMyProfile } from "../api";

export default function ProfileForm(){
  const [form, setForm] = useState({
    job_category: "",
    domain: "",
    years_experience: 0,
    work_mode: "",
    location: "",
    visa_required: "",
    other: ""
  });
  const [msg, setMsg] = useState("");

  useEffect(()=> {
    (async ()=>{
      try{
        const data = await getMyProfile();
        setForm({...form, ...data});
      }catch(e){}
    })();
  },[]);

  async function submit(e){
    e.preventDefault();
    try{
      const res = await saveMyProfile(form);
      setMsg("Profile saved");
    }catch(err){ setMsg(err.detail || "Error saving profile"); }
  }

  function onChange(k,v){ setForm(prev=>({...prev,[k]:v})); }

  return (
    <form onSubmit={submit}>
      <h2>Profile</h2>
      <label>Job Category<input value={form.job_category||""} onChange={e=>onChange("job_category", e.target.value)} /></label>
      <label>Domain<input value={form.domain||""} onChange={e=>onChange("domain", e.target.value)} /></label>
      <label>Years of Experience<input type="number" value={form.years_experience||0} onChange={e=>onChange("years_experience", Number(e.target.value))} /></label>
      <label>Work Mode<input value={form.work_mode||""} onChange={e=>onChange("work_mode", e.target.value)} /></label>
      <label>Location<input value={form.location||""} onChange={e=>onChange("location", e.target.value)} /></label>
      <label>Visa Required<select value={form.visa_required||""} onChange={e=>onChange("visa_required", e.target.value)}>
        <option value="">Select</option>
        <option value="Yes">Yes</option>
        <option value="No">No</option>
        <option value="Maybe">Maybe</option>
      </select></label>
      <label>Other<input value={form.other||""} onChange={e=>onChange("other", e.target.value)} /></label>
      <button type="submit">Save</button>
      <div>{msg}</div>
    </form>
  );
}
