import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const fetchJobs = async () => {
    setLoading(true);
    setError("");
    try {
      const token = localStorage.getItem("access_token");
      const res = await fetch("https://jobright.ai/swan/recommend/landing/jobs", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      const data = await res.json();

      if (!res.ok || !data.jobs) {
        setError("Failed to load jobs. Please try again.");
        setJobs([]);
      } else if (!Array.isArray(data.jobs)) {
        // Ensure jobs is always an array
        setJobs([]);
        setError("No jobs found.");
      } else {
        setJobs(data.jobs);
      }
    } catch (err) {
      console.error(err);
      setError("Failed to load jobs. Please try again.");
      setJobs([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  if (loading) return <div className="p-4">Loading jobs...</div>;

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Job Dashboard</h1>

      {/* Navigation */}
      <div className="flex gap-4 mb-4">
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded"
          onClick={() => navigate("/profile")}
        >
          Profile
        </button>
        <button
          className="bg-gray-500 text-white px-4 py-2 rounded"
          onClick={() => {
            localStorage.removeItem("access_token");
            navigate("/login");
          }}
        >
          Logout
        </button>
      </div>

      {error && <div className="text-red-600 mb-4">{error}</div>}

      {jobs.length === 0 && !error && <div>No jobs found.</div>}

      <div className="grid gap-4">
        {jobs.map((job, idx) => (
          <div
            key={idx}
            className="border p-4 rounded shadow hover:shadow-lg transition"
          >
            <h2 className="text-xl font-semibold">{job.title}</h2>
            <p className="text-gray-600">{job.company}</p>
            <p className="text-gray-500">{job.location}</p>
            {job.score !== undefined && (
              <p className="text-sm text-green-700">Score: {job.score}</p>
            )}
            <a
              href={job.url}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-500 underline"
            >
              Apply
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
