import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { logout } from "../api";

export default function Profile() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState("");
  const [profile, setProfile] = useState({
    email: "",
    domains: [],
    roles: [],
    experience: 0,
    visa_sponsorship: "",
    locations: [],
    work_mode: [],
  });

  const token = localStorage.getItem("access_token");

  // Options for selects
  const domainOptions = ["Finance", "Tech", "Healthcare", "AI/ML"];
  const roleOptions = ["Developer", "Manager", "Analyst", "Data Scientist"];
  const workModeOptions = ["Remote", "Hybrid", "Onsite"];
  const locationOptions = ["Anywhere in US", "California", "Texas", "New York"];
  const visaOptions = ["H1B", "L1", "Green Card", "None"];

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    const fetchProfile = async () => {
      try {
        const res = await fetch("http://localhost:8000/api/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (res.status === 401) {
          navigate("/login");
          return;
        }

        if (!res.ok) {
          setError("Failed to load profile");
          return;
        }

        const data = await res.json();
        setProfile({
          email: data.email || "",
          domains: data.domains || [],
          roles: data.roles || [],
          experience: data.experience || 0,
          visa_sponsorship: data.visa_sponsorship || "",
          locations: data.locations || [],
          work_mode: data.work_mode || [],
        });
      } catch (err) {
        setError("Error fetching profile");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [token, navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile((prev) => ({ ...prev, [name]: value }));
  };

  const handleMultiSelectChange = (name, value) => {
    setProfile((prev) => {
      const list = prev[name];
      if (list.includes(value)) {
        return { ...prev, [name]: list.filter((v) => v !== value) };
      } else {
        return { ...prev, [name]: [...list, value] };
      }
    });
  };

  const handleSave = async () => {
    setSaving(true);
    setError("");
    try {
      const res = await fetch("http://localhost:8000/api/profile/update", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(profile),
      });

      if (!res.ok) {
        const txt = await res.text();
        setError(txt || "Failed to save preferences");
        return;
      }

      alert("Preferences saved successfully!");
    } catch (err) {
      console.error(err);
      setError("Error saving preferences");
    } finally {
      setSaving(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  if (loading) return <p>Loading profile...</p>;

  return (
    <div style={{ padding: 20 }}>
      <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h2>Profile</h2>
        <nav>
          <Link to="/dashboard" style={{ marginRight: 12 }}>Dashboard</Link>
          <Link to="/profile" style={{ marginRight: 12 }}>Profile</Link>
          <button onClick={handleLogout}>Logout</button>
        </nav>
      </header>

      {error && <p style={{ color: "red" }}>{error}</p>}

      <section style={{ marginTop: 20 }}>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={profile.email}
            onChange={handleInputChange}
            disabled
            style={{ display: "block", marginBottom: 12 }}
          />
        </label>

        <label>
          Domains (multi-select):
          {domainOptions.map((d) => (
            <label key={d} style={{ marginLeft: 10 }}>
              <input
                type="checkbox"
                checked={profile.domains.includes(d)}
                onChange={() => handleMultiSelectChange("domains", d)}
              />
              {d}
            </label>
          ))}
        </label>

        <label>
          Roles (multi-select):
          {roleOptions.map((r) => (
            <label key={r} style={{ marginLeft: 10 }}>
              <input
                type="checkbox"
                checked={profile.roles.includes(r)}
                onChange={() => handleMultiSelectChange("roles", r)}
              />
              {r}
            </label>
          ))}
        </label>

        <label style={{ display: "block", marginTop: 12 }}>
          Experience (years):
          <input
            type="number"
            name="experience"
            value={profile.experience}
            onChange={handleInputChange}
            min={0}
          />
        </label>

        <label style={{ display: "block", marginTop: 12 }}>
          Visa Sponsorship:
          <select
            name="visa_sponsorship"
            value={profile.visa_sponsorship}
            onChange={handleInputChange}
          >
            <option value="">Select</option>
            {visaOptions.map((v) => (
              <option key={v} value={v}>{v}</option>
            ))}
          </select>
        </label>

        <label style={{ display: "block", marginTop: 12 }}>
          Location Preferences:
          {locationOptions.map((loc) => (
            <label key={loc} style={{ marginLeft: 10 }}>
              <input
                type="checkbox"
                checked={profile.locations.includes(loc)}
                onChange={() => handleMultiSelectChange("locations", loc)}
              />
              {loc}
            </label>
          ))}
        </label>

        <label style={{ display: "block", marginTop: 12 }}>
          Work Mode:
          {workModeOptions.map((wm) => (
            <label key={wm} style={{ marginLeft: 10 }}>
              <input
                type="checkbox"
                checked={profile.work_mode.includes(wm)}
                onChange={() => handleMultiSelectChange("work_mode", wm)}
              />
              {wm}
            </label>
          ))}
        </label>

        <button
          onClick={handleSave}
          style={{ marginTop: 20 }}
          disabled={saving}
        >
          {saving ? "Saving..." : "Save Preferences"}
        </button>
      </section>
    </div>
  );
}
