import React, { useEffect, useState } from "react";
import axios from "axios";

const Dashboard = ({ userId }) => {
  const [jobs, setJobs] = useState([]);
  const [loadingJobs, setLoadingJobs] = useState(true);
  const [jobsError, setJobsError] = useState(null);

  useEffect(() => {
    const fetchRecommendedJobs = async () => {
      try {
        setLoadingJobs(true);
        const response = await axios.get(
          `http://localhost:8000/api/jobs/recommended?user_id=${userId}`
        );
        setJobs(response.data.jobs);
      } catch (err) {
        console.error(err);
        setJobsError("Failed to fetch jobs.");
      } finally {
        setLoadingJobs(false);
      }
    };

    fetchRecommendedJobs();
  }, [userId]);

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>

      {/* Other dashboard sections go here */}

      <section className="recommended-jobs">
        <h2>Recommended Jobs</h2>

        {loadingJobs && <p>Loading jobs...</p>}
        {jobsError && <p>{jobsError}</p>}
        {!loadingJobs && !jobsError && jobs.length === 0 && (
          <p>No recommended jobs found.</p>
        )}

        <div className="jobs-list">
          {jobs.map((job, index) => (
            <div key={index} className="job-card" style={styles.card}>
              <h3 style={styles.title}>{job.title}</h3>
              <p style={styles.company}>
                {job.company} â€” <span style={styles.source}>{job.source}</span>
              </p>
              {job.domains && job.domains.length > 0 && (
                <p style={styles.domains}>Domains: {job.domains.join(", ")}</p>
              )}
              <a
                href={job.link}
                target="_blank"
                rel="noopener noreferrer"
                style={styles.link}
              >
                View Job
              </a>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

// Simple inline styles for demo (replace with your CSS)
const styles = {
  card: {
    border: "1px solid #ddd",
    padding: "16px",
    borderRadius: "8px",
    marginBottom: "12px",
    backgroundColor: "#f9f9f9",
  },
  title: { margin: 0, fontSize: "18px" },
  company: { margin: "4px 0", color: "#555" },
  source: { fontStyle: "italic", color: "#888" },
  domains: { margin: "4px 0", fontSize: "14px", color: "#666" },
  link: { color: "#1a73e8", textDecoration: "none", fontWeight: "bold" },
};

export default Dashboard;
