import React, { useEffect, useState } from "react";
import { logout } from "../api";
import { Link, useNavigate } from "react-router-dom";

export default function Profile() {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const US_STATES = [
    "Alabama","Alaska","Arizona","Arkansas","California","Colorado","Connecticut",
    "Delaware","Florida","Georgia","Hawaii","Idaho","Illinois","Indiana","Iowa",
    "Kansas","Kentucky","Louisiana","Maine","Maryland","Massachusetts","Michigan",
    "Minnesota","Mississippi","Missouri","Montana","Nebraska","Nevada","New Hampshire",
    "New Jersey","New Mexico","New York","North Carolina","North Dakota","Ohio",
    "Oklahoma","Oregon","Pennsylvania","Rhode Island","South Carolina","South Dakota",
    "Tennessee","Texas","Utah","Vermont","Virginia","Washington","West Virginia",
    "Wisconsin","Wyoming",
  ];

  useEffect(() => {
    const load = async () => {
      const token = localStorage.getItem("access_token");
      if (!token) return navigate("/login");

      const res = await fetch("http://localhost:8000/api/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (res.status === 401) return navigate("/login");

      const data = await res.json();
      setProfile(data);
      setLoading(false);
    };

    load();
  }, []);

  function handleChange(e) {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  }

  function handleMultiSelect(e) {
    const options = [...e.target.options]
      .filter((o) => o.selected)
      .map((o) => o.value);

    setProfile({ ...profile, [e.target.name]: options });
  }

  async function saveProfile() {
    const token = localStorage.getItem("access_token");

    const res = await fetch("http://localhost:8000/api/profile/update", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(profile),
    });

    if (res.ok) {
      alert("Profile updated successfully!");
    } else {
      alert("Failed to update profile.");
    }
  }

  function handleLogout() {
    logout();
    navigate("/login");
  }

  if (loading) return <div style={{ padding: 20 }}>Loading profile...</div>;

  return (
    <div style={{ padding: 20 }}>
      <header
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <h2>Profile</h2>
        <nav>
          <Link to="/dashboard" style={{ marginRight: 12 }}>
            Dashboard
          </Link>
          <Link to="/profile" style={{ marginRight: 12 }}>
            Profile
          </Link>
          <button onClick={handleLogout}>Logout</button>
        </nav>
      </header>

      <section style={{ marginTop: 30, maxWidth: 600 }}>
        <h3>User Preferences</h3>

        {/* Email */}
        <label style={{ display: "block", marginTop: 20 }}>Email</label>
        <input
          type="email"
          value={profile.email}
          readOnly
          style={{ width: "100%", padding: 8 }}
        />

        {/* Domain */}
        <label style={{ display: "block", marginTop: 20 }}>Domain</label>
        <select
          multiple
          name="domains"
          value={profile.domains || []}
          onChange={handleMultiSelect}
          style={{ width: "100%", padding: 8, height: 100 }}
        >
          <option>Software Engineering</option>
          <option>Data Science</option>
          <option>AI / ML</option>
          <option>Cloud</option>
          <option>Cybersecurity</option>
          <option>Finance / FinTech</option>
          <option>Business / Product</option>
        </select>

        {/* Roles */}
        <label style={{ display: "block", marginTop: 20 }}>Preferred Roles</label>
        <select
          multiple
          name="roles"
          value={profile.roles || []}
          onChange={handleMultiSelect}
          style={{ width: "100%", padding: 8, height: 100 }}
        >
          <option>Software Engineer</option>
          <option>Data Scientist</option>
          <option>ML Engineer</option>
          <option>Full Stack Engineer</option>
          <option>Backend Engineer</option>
          <option>Cloud Engineer</option>
          <option>AI Researcher</option>
        </select>

        {/* Experience */}
        <label style={{ display: "block", marginTop: 20 }}>Experience (years)</label>
        <input
          type="number"
          name="experience"
          value={profile.experience || ""}
          onChange={handleChange}
          style={{ width: "100%", padding: 8 }}
        />

        {/* Visa Sponsorship */}
        <label style={{ display: "block", marginTop: 20 }}>Visa Sponsorship</label>
        <select
          name="visa"
          value={profile.visa || ""}
          onChange={handleChange}
          style={{ width: "100%", padding: 8 }}
        >
          <option value="">Select</option>
          <option value="yes">Yes, I need sponsorship</option>
          <option value="no">No, I do not need sponsorship</option>
        </select>

        {/* Location Preference */}
        <label style={{ display: "block", marginTop: 20 }}>Location Preference</label>
        <select
          name="location_type"
          value={profile.location_type || ""}
          onChange={handleChange}
          style={{ width: "100%", padding: 8 }}
        >
          <option value="">Select</option>
          <option value="anywhere">Anywhere in US</option>
          <option value="states">Specific States</option>
        </select>

        {/* US State Selector */}
        {profile.location_type === "states" && (
          <>
            <label style={{ display: "block", marginTop: 20 }}>Select States</label>
            <select
              multiple
              name="states"
              value={profile.states || []}
              onChange={handleMultiSelect}
              style={{ width: "100%", padding: 8, height: 150 }}
            >
              {US_STATES.map((s) => (
                <option key={s} value={s}>
                  {s}
                </option>
              ))}
            </select>
          </>
        )}

        {/* Work Mode */}
        <label style={{ display: "block", marginTop: 20 }}>Work Mode</label>
        <select
          multiple
          name="work_mode"
          value={profile.work_mode || []}
          onChange={handleMultiSelect}
          style={{ width: "100%", padding: 8, height: 100 }}
        >
          <option>Remote</option>
          <option>Hybrid</option>
          <option>Onsite</option>
        </select>

        <button
          onClick={saveProfile}
          style={{
            marginTop: 30,
            padding: "10px 20px",
            background: "black",
            color: "white",
            border: "none",
            cursor: "pointer",
          }}
        >
          Save Profile
        </button>
      </section>
    </div>
  );
}
