import React, {useEffect,useState} from "react";
import { fetchJobs, getProfile } from "../api";
import { Link, useNavigate } from "react-router-dom";

export default function Dashboard(){
  const [jobs,setJobs] = useState([]);
  const [loading,setLoading]=useState(true);
  const nav = useNavigate();

  useEffect(()=>{
    // ensure logged in
    getProfile().then(p=>{
      if(!p || !p.id){ nav("/login"); return; }
      // fetch jobs
      fetchJobs().then(data=>{
        // API returns array
        setJobs(Array.isArray(data) ? data : (data.jobs || []));
      }).catch(()=>setJobs([])).finally(()=>setLoading(false));
    }).catch(()=>nav("/login"));
  },[nav]);

  return (
    <div className="container">
      <nav><Link to="/dashboard">Dashboard</Link> | <Link to="/profile">Profile</Link></nav>
      <h2>Recommended Jobs</h2>
      {loading ? <p>Loading jobs...</p> : (
        jobs.length === 0 ? <p>No matching jobs found.</p> : (
          jobs.map((j, idx)=>(
            <div key={idx} className="job-card">
              <strong>{j.title}</strong><br/>
              <small>{j.company} • {j.source} • {j.posted || ""}</small><br/>
              <a href={j.url} target="_blank" rel="noreferrer">Apply</a>
            </div>
          ))
        )
      )}
    </div>
  );
}
