import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Dashboard() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) { navigate("/login"); return; }

    fetch("http://localhost:8000/api/jobs/", { headers: { Authorization: `Bearer ${token}` } })
      .then(r => r.json())
      .then(data => {
        setJobs(data.jobs || []);
      })
      .catch(err => console.error(err))
      .finally(() => setLoading(false));
  }, [navigate]);

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ maxWidth: 800, margin: "auto", padding: 20 }}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h2>Dashboard</h2>
        <div>
          <Link to="/preferences" style={{ marginRight: 10 }}>Preferences</Link>
          <button onClick={() => { localStorage.removeItem("token"); navigate("/login"); }}>Logout</button>
        </div>
      </div>

      {jobs.length === 0 ? (
        <p>No matching jobs found. Make sure your preferences are set.</p>
      ) : (
        jobs.map((job, i) => (
          <div key={i} style={{ border: "1px solid #ddd", padding: 12, marginBottom: 8 }}>
            <h3>{job.title}</h3>
            <div>{job.company}</div>
            <div>{job.location} â€¢ {job.work_mode}</div>
            <a href={job.url} target="_blank" rel="noreferrer">Open</a>
          </div>
        ))
      )}
    </div>
  );
}
