// frontend/src/pages/register.jsx
import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { registerUser } from "../api";

export default function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [err, setErr] = useState("");
  const nav = useNavigate();

  async function submit(e) {
  e.preventDefault();
  try {
    const res = await registerUser({ name, email, password });
    if (res.status === 200) {
      alert("Registered successfully!");
      navigate("/login"); // redirect to login page
    }
  } catch (err) {
    if (err.response && err.response.status === 400) {
      alert("Email already registered!");
    } else {
      alert("Registration failed");
    }
  }
}


  return (
    <div className="container">
      <h2>Register</h2>
      <form onSubmit={submit}>
        <div className="form-row"><input placeholder="Full name" value={name} onChange={e=>setName(e.target.value)} /></div>
        <div className="form-row"><input placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} required /></div>
        <div className="form-row"><input placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} required /></div>
        <div className="form-row"><input placeholder="Confirm password" type="password" value={confirm} onChange={e=>setConfirm(e.target.value)} required /></div>
        <div className="form-row"><button className="primary" type="submit">Register</button></div>
      </form>
      <p>Already registered? <Link to="/login">Login</Link></p>
      {err && <p style={{color:"red"}}>{err}</p>}
    </div>
  );
}
