import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { logout } from "../api";

export default function Profile() {
  const [email, setEmail] = useState("");
  const [domains, setDomains] = useState([]);
  const [roles, setRoles] = useState([]);
  const [experience, setExperience] = useState(0);
  const [visa, setVisa] = useState("");
  const [locations, setLocations] = useState([]);
  const [workMode, setWorkMode] = useState([]);

  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const token = localStorage.getItem("access_token"); // IMPORTANT

  // Multi-select handler
  const toggleValue = (arr, value, setFn) => {
    if (arr.includes(value)) {
      setFn(arr.filter((v) => v !== value));
    } else {
      setFn([...arr, value]);
    }
  };

  // Load profile on mount
  useEffect(() => {
    if (!token) return navigate("/login");

    const loadProfile = async () => {
      try {
        const res = await fetch("http://localhost:8000/api/profile/", {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (res.status === 401) return navigate("/login");

        const data = await res.json();

        // Your schema gives:
        // email, domains, roles, experience, locations, visa_sponsorship, work_mode
        setEmail(data.email || "");
        setDomains(data.domains || []);
        setRoles(data.roles || []);
        setExperience(data.experience || 0);
        setVisa(data.visa_sponsorship || "");
        setLocations(data.locations || []);
        setWorkMode(data.work_mode || []);

        setLoading(false);
      } catch (err) {
        console.error("Profile load failed:", err);
      }
    };

    loadProfile();
  }, []);

  async function handleSave() {
    try {
      const res = await fetch("http://localhost:8000/api/profile/update", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          domains,
          roles,
          experience,
          visa_sponsorship: visa,
          locations,
          work_mode: workMode,
        }),
      });

      if (!res.ok) {
        const txt = await res.text();
        alert("Failed to save: " + txt);
        return;
      }

      alert("Profile saved successfully!");
    } catch (err) {
      alert("Error saving profile");
      console.error(err);
    }
  }

  function handleLogout() {
    logout();
    navigate("/login");
  }

  if (loading) return <p>Loading profile...</p>;

  return (
    <div style={{ padding: "20px", maxWidth: 600, margin: "0 auto" }}>
      <header
        style={{
          display: "flex",
          justifyContent: "space-between",
          marginBottom: 20,
        }}
      >
        <h2>Profile</h2>
        <nav>
          <Link to="/dashboard" style={{ marginRight: 20 }}>
            Dashboard
          </Link>
          <button onClick={handleLogout}>Logout</button>
        </nav>
      </header>

      {/* EMAIL (read-only) */}
      <div style={{ marginBottom: 15 }}>
        <label>Email</label>
        <input
          value={email}
          readOnly
          style={{ width: "100%", padding: 8, marginTop: 5 }}
        />
      </div>

      {/* DOMAINS MULTISELECT */}
      <div style={{ marginBottom: 15 }}>
        <label>Domains (Multi-select)</label>
        <div>
          {["AI/ML", "Backend", "Frontend", "Data Science", "Cloud"].map(
            (d) => (
              <label key={d} style={{ display: "block" }}>
                <input
                  type="checkbox"
                  checked={domains.includes(d)}
                  onChange={() => toggleValue(domains, d, setDomains)}
                />
                {d}
              </label>
            )
          )}
        </div>
      </div>

      {/* ROLES MULTISELECT */}
      <div style={{ marginBottom: 15 }}>
        <label>Roles (Multi-select)</label>
        <div>
          {["Software Engineer", "Data Scientist", "ML Engineer", "Full-stack"].map(
            (r) => (
              <label key={r} style={{ display: "block" }}>
                <input
                  type="checkbox"
                  checked={roles.includes(r)}
                  onChange={() => toggleValue(roles, r, setRoles)}
                />
                {r}
              </label>
            )
          )}
        </div>
      </div>

      {/* EXPERIENCE */}
      <div style={{ marginBottom: 15 }}>
        <label>Experience (years)</label>
        <input
          type="number"
          value={experience}
          onChange={(e) => setExperience(Number(e.target.value))}
          style={{ width: "100%", padding: 8 }}
        />
      </div>

      {/* VISA */}
      <div style={{ marginBottom: 15 }}>
        <label>Visa Sponsorship</label>
        <select
          value={visa}
          onChange={(e) => setVisa(e.target.value)}
          style={{ width: "100%", padding: 8 }}
        >
          <option value="">Select</option>
          <option value="Required">Required</option>
          <option value="Not Required">Not Required</option>
        </select>
      </div>

      {/* LOCATIONS MULTISELECT */}
      <div style={{ marginBottom: 15 }}>
        <label>Location Preferences</label>
        <div>
          {["Anywhere in US", "Texas", "California", "New York", "Florida"].map(
            (l) => (
              <label key={l} style={{ display: "block" }}>
                <input
                  type="checkbox"
                  checked={locations.includes(l)}
                  onChange={() => toggleValue(locations, l, setLocations)}
                />
                {l}
              </label>
            )
          )}
        </div>
      </div>

      {/* WORK MODE MULTISELECT */}
      <div style={{ marginBottom: 15 }}>
        <label>Work Mode</label>
        <div>
          {["Remote", "Hybrid", "Onsite"].map((w) => (
            <label key={w} style={{ display: "block" }}>
              <input
                type="checkbox"
                checked={workMode.includes(w)}
                onChange={() => toggleValue(workMode, w, setWorkMode)}
              />
              {w}
            </label>
          ))}
        </div>
      </div>

      <button
        onClick={handleSave}
        style={{ padding: "10px 20px", fontSize: 16 }}
      >
        Save Preferences
      </button>
    </div>
  );
}
