import React, { useEffect, useState } from "react";
import { getProfile, saveProfile } from "../api";
import { useNavigate } from "react-router-dom";

export default function Profile() {
  const [profile, setProfile] = useState({
    domain: "",
    role: "",
    experience: "",
    location: "",
    visa_sponsorship: "",
    work_mode: ""
  });
  const navigate = useNavigate();

  // Redirect if not logged in and fetch profile
  useEffect(() => {
    getProfile()
      .then(data => {
        if (!data || !data.id) {
          navigate("/login"); // redirect to login if not authenticated
        } else {
          setProfile(data);
        }
      })
      .catch(err => {
        console.error("Error fetching profile:", err);
        navigate("/login"); // redirect on error
      });
  }, [navigate]);

  // Save profile handler
  async function submit(e) {
    e.preventDefault();
    try {
      const res = await saveProfile(profile);
      if (res && res.success) {
        alert("Profile saved successfully!");
      } else {
        alert("Failed to save profile.");
      }
    } catch (err) {
      console.error("Error saving profile:", err);
      alert("Error saving profile.");
    }
  }

  return (
    <div className="container">
      <nav>
        <button onClick={() => navigate("/dashboard")}>Dashboard</button>
        <button onClick={() => navigate("/profile")}>Profile</button>
      </nav>

      <h2>Your Preferences</h2>
      <form onSubmit={submit}>
        <input
          placeholder="Domain"
          value={profile.domain}
          onChange={e => setProfile({ ...profile, domain: e.target.value })}
        />
        <input
          placeholder="Role"
          value={profile.role}
          onChange={e => setProfile({ ...profile, role: e.target.value })}
        />
        <input
          placeholder="Experience (years)"
          value={profile.experience}
          onChange={e => setProfile({ ...profile, experience: e.target.value })}
        />
        <input
          placeholder="Location"
          value={profile.location}
          onChange={e => setProfile({ ...profile, location: e.target.value })}
        />
        <select
          value={profile.work_mode}
          onChange={e => setProfile({ ...profile, work_mode: e.target.value })}
        >
          <option value="">Select Work Mode</option>
          <option value="Remote">Remote</option>
          <option value="Hybrid">Hybrid</option>
          <option value="Onsite">Onsite</option>
        </select>
        <select
          value={profile.visa_sponsorship}
          onChange={e =>
            setProfile({ ...profile, visa_sponsorship: e.target.value })
          }
        >
          <option value="">Visa Sponsorship?</option>
          <option value="Yes">Yes</option>
          <option value="No">No</option>
        </select>

        <button type="submit">Save</button>
      </form>
    </div>
  );
}
